import React, { Component } from "react";
import { getFavoriteShowsByUserId } from "./helpers";


export class ProfileHome extends Component {
  state = {
    loading: true,
    error: false,
    data: [],
  }

  async componentDidMount() {
    const shows = await getFavoriteShowsByUserId(this.props.userId);
    const plays = shows.filter((show) => {
      return show.category.includes("broadway" && "play")
    });
    const sortedPlays = plays.sort((a, b) => {
      return b.rating - a.rating;
    });
    const playsArray = sortedPlays.map(play => Object.values(play));
    this.setState({ loading: false, data: playsArray })
  }

  render() {
    return (
      <div>
        {this.props.children({
          ...this.props,
          ...this.state,
        })}
      </div>
    );
  }

};
